    //significa que llego un codigo nuevo que meteremos en el
    //buffer (solo si no está repetido)
    node.warn(msg.payload);
    // verificamos que no esté repetido
    if ((buffer.indexOf(msg.payload) == -1) && (msg.payload != flow.get("ultimoID"))) {
        // metemos en el buffer

        buffer.push(msg.payload);
        flow.set("buffer", buffer);

    }
}

if (flow.get("habilitador") == 1) {
    //significa que el usuario tocó un botón
    //por lo tanto, puedo sacar del buffer un nuevo mensaje

    id = buffer.shift();
    flow.set("buffer", buffer);

    if (id != undefined){
        flow.set("habilitador", 0);
        msg.payload = `El dispositivo OMC-WIFI-${id} 
                    se está intentando asociar con el servidor. 
                    ¿Desea agregar dicho dispositivo?`;
        msg.topic = "Dispositivo nuevo"

        flow.set("ultimoID", id);
        return msg;
    }
}

if (msg.payload != "habilitador") {
    //significa que el mensaje es de cambio

    //guardamos dicho mensaje solo para enviarse 
    //cuando llegue un habilitador
    flow.set("cambio", msg.payload);

    //el mensaje que mandaremos al siguiente nodo será nulo
    msg.payload = null;
}

else {
    //significa que el mensaje es un habilitador

    if (flow.get("cambio") != null) {
        //si tengo cambio entonces lo paso al siguiente nodo
        msg.payload = flow.get("cambio");
        //y digo que ahora no tengo cambio
        flow.set("cambio", null);
        return msg;
